import React, {Component} from 'react';
import AppHeader from "../AppHeader";
import SearchPanel from "../SearchPanel";
import TodoFilter from "../TodoFilter";
import TodoList from "../TodoList";
import {StyledApp, StyledSearchBar} from "./App.styled";
import AddItem from "../AddItem";

class App extends Component {

    state = {
        todoData: [
            {label: 'Learn React', important: false, done: false, id: 1},
            {label: 'Drink Coffee', important: false, done: false, id: 2},
            {label: 'Feed Cat', important: false, done: false, id: 3},
        ]
    };

    onDelete = (id) => {
        this.setState((state) => {
            let shallowCopy = [...state.todoData];
            let result = shallowCopy.filter(el => el.id !==id);
            return {
                todoData: result,
            }
        })
    };

    onImportantClick = () => {
        this.setState(({important}) => {
            return {
                important: !important,
            }
        })
    };

    onDoneClick = () => {
        this.setState(({done}) => {
            return {
                done: !done,
            }
        })
    };

    onSubmit = (e, label, func) => {

        e.preventDefault();
        func();
        if (label.length !== 0) {
            const newLabel = this.newTodoItem(label);
            this.setState(({todoData}) => {

                const newData = [
                    ...todoData,
                    newLabel,
                ];

                return {
                    todoData: newData,
                }
            });
        }
    };

    newTodoItem (label) {
        return {
            label: label,
            important: false,
            id: Math.trunc(Math.random()*10000),
        }
    }



    render() {

        return (
            <StyledApp>
                <AppHeader/>
                <StyledSearchBar>
                    <SearchPanel/>
                    <TodoFilter/>
                </StyledSearchBar>
                <TodoList
                    data = {this.state.todoData}
                    onDoneClick = {() => this.onDoneClick()}
                    onImportantClick = {() => this.onImportantClick()}
                    onDelete = {(id) => this.onDelete(id)}
                    checkLength = {this.state.todoData.length}/>
                    <AddItem onSubmit = {this.onSubmit}/>
            </StyledApp>
        )
    }
}

export default App;
