import React, {Component} from 'react';
import styled from "styled-components";
import {StyledButton, StyledListItem} from "../TodoList/TodoList.styled";


export default class TodoListItem extends Component {


    state = {
        done: false,
        important: false,
    };


    onImportantClick = () => {
        this.setState(({important}) => {
            return {
                important: !important,
            }
        })
    };

    onLabelClick = () => {
        this.setState(({done}) => {
            return {
                done: !done,
            }
        })
    };



    render() {

        const StyledListItemSpan = styled.span`
          text-decoration: ${props => props.done ? 'line-through' : undefined};
          display: block;
          background: ${props => props.important ? 'black' : undefined};
        `;

        const {label, important} = this.props;
        return (
            <StyledListItemSpan done = {this.state.done} onClick={() => this.onLabelClick()} important = {important}>

                {label}

                <StyledButton trash>
                    <i className="far fa-trash-alt"> </i>
                </StyledButton>

                <StyledButton onClick = {() => this.onImportantClick()}>
                    <i className="fas fa-exclamation"> </i>
                </StyledButton>

            </StyledListItemSpan>
        );
    }

}
