import React, {Component} from 'react';
import {StyledButton, StyledListItemSpan} from "./TodoListItem.styled";


export default class TodoListItem extends Component {


    state = {
        done: false,
        important: false,
    };


    onImportantClick = () => {
        this.setState(({important}) => {
            return {
                important: !important,
            }
        })
    };

    onLabelClick = () => {
        this.setState(({done}) => {
            return {
                done: !done,
            }
        })
    };



    render() {

        const {label} = this.props;
        return (
            <StyledListItemSpan done = {this.state.done} onClick={() => this.onLabelClick()} important = {this.state.important}>

                {label}

                <StyledButton trash>
                    <i className="far fa-trash-alt"> </i>
                </StyledButton>

                <StyledButton onClick = {() => this.onImportantClick()}>
                    <i className="fas fa-exclamation"> </i>
                </StyledButton>

            </StyledListItemSpan>
        );
    }

}
