import React, {Component} from 'react';
import {StyledButton, StyledListItemSpan} from "./TodoListItem.styled";


export default class TodoListItem extends Component {


    render() {

        const {label, id, onDelete, onImportantClick, onDoneClick} = this.props;
            return (
                <React.Fragment>
                    <StyledListItemSpan
                        onClick={() => onDoneClick()}>
                        {label}
                    </StyledListItemSpan>

                    <StyledButton trash onClick = {() => onDelete(id)}>
                        <i className="far fa-trash-alt"> </i>
                    </StyledButton>

                    <StyledButton onClick = {() => onImportantClick()}>
                        <i className="fas fa-exclamation"> </i>
                    </StyledButton>
                </React.Fragment>
            );

        }
}
