import React, {Component} from 'react';
import AppHeader from "../AppHeader";
import SearchPanel from "../SearchPanel";
import TodoFilter from "../TodoFilter";
import TodoList from "../TodoList";
import {StyledApp, StyledSearchBar} from "./App.styled";
import AddItem from "../AddItem";

class App extends Component {

    state = {
        todoData: [

        ]
    };

    onDelete = (id) => {
        this.setState((state) => {
            let shallowCopy = [...state.todoData];
            let result = shallowCopy.filter(el => el.id !==id);
            return {
                todoData: result,
            }
        })
    };


    onSubmit = (e, label, func) => {

        e.preventDefault();
        func();
        if (label.length !== 0) {
            const newLabel = this.newTodoItem(label);
            this.setState(({todoData}) => {

                const newData = [
                    ...todoData,
                    newLabel,
                ];

                return {
                    todoData: newData,
                }
            });
        }
    };

    newTodoItem (label) {
        return {
            label: label,
            important: false,
            id: Math.trunc(Math.random()*10000),
        }
    }



    render() {

        return (
            <StyledApp>
                <AppHeader/>
                <StyledSearchBar>
                    <SearchPanel/>
                    <TodoFilter/>
                </StyledSearchBar>
                <TodoList data={this.state.todoData} onDelete={(id) =>
                    this.onDelete(id)} checkLength = {this.state.todoData.length}/>
                    <AddItem onSubmit = {this.onSubmit}/>
            </StyledApp>
        )
    }
}

export default App;
