import React, {Component} from 'react';
import { StyledListItem } from '../TodoList/TodoList.styled'


export default class TodoListItem extends Component {

    onLabelClick = () => {
        this.setState(({done}) => {
            return {
                done: !done,
            }
        })
    };


    state = {
        done: false
    };

    render() {

        const {label} = this.props;
        return (
            <StyledListItem done = {this.state.done} onClick={() => this.onLabelClick()}>

                {label}
            </StyledListItem>
        );
    }

}
