import styled from "styled-components";

const StyledListItem = styled.li`
    display: flex;
    justify-content: space-between;
    list-style: none;
    padding: .75rem 1.25rem;
    border-bottom: 1px solid rgba(0,0,0,.125);
    border-color: white;
    color: white;
    cursor: pointer;
    align-items: center;
    &:last-of-type {
      border-bottom: none;
    }
    &:hover {
      background: rgb(6,6,6);
      background: linear-gradient(120deg, rgba(131,58,180,1) 0%, rgba(191,42,166,1) 50%, rgba(255,24,19,1) 100%);
    }
`;

const StyledList = styled.ul`
  padding: 0;
  border-radius: 5px;
  border: 2px solid white;
`;

const StyledButton = styled.button`
  background: none;
  border: 1px solid white;
  border-radius: 5px;
  margin-right: 7px;
  width: 30px;
  height: 30px;
  outline: none;
  cursor: pointer;
  color: white;
  font-size: 11px;
  &:first-of-type {
    margin-left: auto;
  }
  &:hover {
    transition-duration: .3s;
    background: ${props => props.trash ? 
    'linear-gradient(120deg, rgba(20,218,87,0.55) 0%, rgba(42,191,188,0.55) 49%, rgba(4,226,95,0.55) 100%)' :
    'linear-gradient(120deg, rgba(247,255,0,0.55) 0%, rgba(210,135,17,0.55) 49%, rgba(255,250,3,0.55) 100%)'};
    border: ${props => props.trash ? '1px solid green;' : '1px solid yellow'};
  }
`;


export {StyledList, StyledListItem, StyledButton};
