import React, {Component} from 'react';
import AppHeader from "../AppHeader";
import SearchPanel from "../SearchPanel";
import TodoFilter from "../TodoFilter";
import TodoList from "../TodoList";
import {StyledApp, StyledSearchBar} from "./App.styled";
import AddItem from "../AddItem";

class App extends Component {

    state = {
        todoData: [
            {label: 'Learn React', important: false, id: 1},
            {label: 'Drink Coffee', important: false, id: 2},
            {label: 'Feed Cat', important: false, id: 3},
        ]
    };

    onDelete = (id) => {
        this.setState((state) => {
            let shallowCopy = [...state.todoData];
            let result = shallowCopy.filter(el => el.id !==id);
            return {
                todoData: result,
            }
        })
};

    onSubmit = (e, label) => {
        e.preventDefault();
        const newLabel = this.newTodoItem(label);
        // this.setState(({todoData}) => {
        //     const newData = [
        //         ...todoData
        //     ]
        // })
        console.log(newLabel);
    };

    newTodoItem (label) {
        return {
            label: label,
            important: false,
            id: Math.trunc(Math.random()*10000),
        }
    }



    render() {

        return (
            <StyledApp>
                <AppHeader/>
                <StyledSearchBar>
                    <SearchPanel/>
                    <TodoFilter/>
                </StyledSearchBar>
                <TodoList data={this.state.todoData} onDelete={(id) =>
                    this.onDelete(id)} checkLength = {this.state.todoData.length}/>
                    <AddItem onSubmit = {this.onSubmit}/>
            </StyledApp>
        )
    }
}

export default App;
