import React, {Component} from 'react';
import {StyledButton, StyledListItemSpan} from "./TodoListItem.styled";


export default class TodoListItem extends Component {


    state = {
        done: false,
        important: false,
    };


    onImportantClick = () => {
        this.setState(({important}) => {
            return {
                important: !important,
            }
        })
    };

    onLabelClick = () => {
        this.setState(({done}) => {
            return {
                done: !done,
            }
        })
    };



    render() {

        const {label, id, onDelete, checkLength} = this.props;
        if (checkLength !== 0) {
            return (
                <React.Fragment>
                    <StyledListItemSpan done = {this.state.done} onClick={() => this.onLabelClick()} important = {this.state.important}>
                        {label}
                    </StyledListItemSpan>

                    <StyledButton trash onClick = {() => onDelete(id)}>
                        <i className="far fa-trash-alt"> </i>
                    </StyledButton>

                    <StyledButton onClick = {() => this.onImportantClick()}>
                        <i className="fas fa-exclamation"> </i>
                    </StyledButton>
                </React.Fragment>
            );
            if (checkLength === 1) {
                return (
                    console.log('fw')
                )
            }
        }


    }

}
