import styled from "styled-components";

const StyledListItem = styled.li`
    display: flex;
    justify-content: space-between;
    list-style: none;
    padding: .75rem 1.25rem;
    border-bottom: 1px solid rgba(0,0,0,.125);
    border-color: white;
    color: white;
    cursor: pointer;
    background: ${props => props.center ? 'rgba(0, 44, 44, 0.6);' : undefined};
    &:last-of-type {
      border-bottom: none;
    }
    &:hover {
      background: rgb(6,6,6);
      background: linear-gradient(120deg, rgba(131,58,180,1) 0%, rgba(191,42,166,1) 50%, rgba(255,24,19,1) 100%);
    }
`;

const StyledList = styled.ul`
  padding: 0;
  border-radius: 5px;
  border: 2px solid white;
`;


export {StyledList, StyledListItem};
