import React, {Component} from 'react'
import TodoListItem from "../TodoListItem";
import {StyledListItem, StyledList, StyledButton} from "./TodoList.styled";
import * as PropTypes from "prop-types";


class TodoList extends Component {

    state = {
        done: false,
        important: false,
    };


    onImportantClick = () => {
        this.setState(({important}) => {
            return {
                important: !important,
            }
        })
    };

    render() {
        let {data} = this.props;
        console.log(data);

        const elements = data.map((item) => {
            const {id, ...itemProps} = item;

            return (
                <StyledListItem key={id}>

                    <TodoListItem state = {this.state} {...itemProps} important = {this.state.important}/>

                    <StyledButton trash>
                        <i className="far fa-trash-alt"> </i>
                    </StyledButton>

                    <StyledButton onClick = {() => this.onImportantClick()}>
                        <i className="fas fa-exclamation"> </i>
                    </StyledButton>

                </StyledListItem>
            );
        });

        return (
            <StyledList>
                {elements}
            </StyledList>
        );
    }
}

export default TodoList;
