import React, {Component} from 'react';
import AppHeader from "../AppHeader";
import SearchPanel from "../SearchPanel";
import TodoFilter from "../TodoFilter";
import TodoList from "../TodoList";
import {StyledApp, StyledSearchBar} from "./App.styled";

class App extends Component {

    state = {
        todoData: [
            {label: 'Learn React', important: false, id: 1},
            {label: 'Drink Coffee', important: false, id: 2},
            {label: 'Feed Cat', important: false, id: 3},
        ]
    };

    onDelete = (id) => {
        this.setState((state) => {
            let shallowCopy = [...state.todoData];
            let result = shallowCopy.filter(el => el.id !==id);

            return {
                todoData: result,
            }
        })
};


    render() {

        return (
            <StyledApp>
                <AppHeader/>
                <StyledSearchBar>
                    <SearchPanel/>
                    <TodoFilter/>
                </StyledSearchBar>
                <TodoList data={this.state.todoData} onDelete={(id) => this.onDelete(id)}
                />
            </StyledApp>
        )
    }
}

export default App;
