import styled from "styled-components";

const StyledListItem = styled.li`
    list-style: none;
    padding: .75rem 1.25rem;
    border-bottom: 1px solid rgba(0,0,0,.125);
    border-color: white;
    color: white;
    cursor: pointer;
    &:last-of-type {
      border-bottom: none;
    }
    &:hover {
      background: rgb(6,6,6);
      background: linear-gradient(120deg, rgba(131,58,180,1) 0%, rgba(191,42,166,1) 50%, rgba(255,24,19,1) 100%);
    }
`;

const StyledList = styled.ul`
  padding: 0;
  border-radius: 5px;
  border: 2px solid white;
`;

const StyledButton = styled.button`
  background: none;
  border: 1px solid white;
  border-radius: 5px;
  margin-right: 5px;
  width: 30px;
  height: 30px;
  outline: none;
  cursor: pointer;
  &:hover {
    border: 1px solid green;
    transition-duration: .3s;
    background-color: rgba(54, 207, 44, 0.6);
  }
`;


export {StyledList, StyledListItem, StyledButton};
