import React from 'react'
import styled from "styled-components";

const StyledButtonFilters = styled.button`
  display: inline-block;
  padding: 5px 15px;
  margin-left: 5px;
  font-family:'Segoe UI','Roboto',sans-serif;
  color: white;
  background: none;
  border: 1px solid white;
  border-radius: 5px;
  outline: none;
  cursor: pointer;
  font-size: 15px;
  &:first-of-type {
    margin-left: auto;
  }
  &:hover {
    border: 1px solid #ccc912;
    transition-duration: .3s;
  }
`;

const TodoFilter = () => {
    return (
        <React.Fragment>
            <StyledButtonFilters active>App</StyledButtonFilters>
            <StyledButtonFilters>Active</StyledButtonFilters>
            <StyledButtonFilters>Done</StyledButtonFilters>
        </React.Fragment>
    )
};

export default TodoFilter;
