import React from 'react'
import styled from "styled-components";

const StyledButton = styled.button`
  display: inline-block;
  padding: 5px 15px;
  margin-left: 5px;
  font-family:'Segoe UI','Roboto',sans-serif;
  color: white;
  background: ${props => props.active ? 'deepskyblue' : 'none'};
  border: 1px solid white;
  border-radius: 5px;
  outline: none;
  cursor: pointer;
  font-size: 15px;
  &:hover {
    background-color: #004eff;
    transition-duration: .3s;
    border-color: #3dcca2;
  }
`;

const TodoFilter = () => {
    return (
        <React.Fragment>
            <StyledButton active>App</StyledButton>
            <StyledButton>Active</StyledButton>
            <StyledButton>Done</StyledButton>
        </React.Fragment>
    )
};

export default TodoFilter;
