import React, {Component} from 'react';
import styled from "styled-components";


export default class TodoListItem extends Component {

    constructor(props) {
        super(props)
        this.state = this.props.state
    }

    onLabelClick = () => {
        this.setState(({done}) => {
            return {
                done: !done,
            }
        })
    };



    render() {

        const StyledListItemSpan = styled.span`
          text-decoration: ${props => props.done ? 'line-through' : undefined};
          display: block;
          background: ${props => props.important ? 'black' : undefined};

        `;

        const {label, important} = this.props;
        return (
            <StyledListItemSpan done = {this.state.done} onClick={() => this.onLabelClick()} important = {important}>
                {label}
            </StyledListItemSpan>
        );
    }

}
